import { createStore } from 'redux';

const initialState = {
    movies: [],
    shows: []
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_MOVIES":
            return {
                ...state,
                movies: payload
            }
        default:
            return state;
    }
}

const makeStore = createStore(reducer);

export default makeStore;
