import Fetch from 'isomorphic-unfetch';
import Layout from "../components/Layout";
import { API_KEY } from '../API_KEY';

const List = props => {
    console.log(props.movieInfo[0].overview.length)
    return (
        <Layout>
            <h1 className="text-center">Movie list!</h1>
            <div className="grid">
                {props.movieInfo.map(movie => (
                    <div className="card mb-3 text-center">
                        <h3 className="card-header">{movie.title.length > 18 ? movie.title.substring(0, 18) + '...' : movie.title}</h3>
                        <div className="card-body">
                            <h6 className="card-subtitle text-muted">{movie.overview.length > 300 ? movie.overview.substring(0, 300) : movie.overview}</h6>
                        </div>
                        <img src={`https://image.tmdb.org/t/p/original/${movie.backdrop_path}`} alt="Card image" />
                    </div>
                ))}
            </div>
            <style jsx>{`
                h1 {
                    margin: 20px auto;
                }
                .grid {
                    display: grid;
                    grid-template-columns: 1fr 1fr 1fr;
                    grid-gap: 15px;
                }
                .card img {
                    height: 200px;
                    width: 100%;
                    display: block;
                }
            `}

            </style>
        </Layout>
    )
}

List.getInitialProps = async () => {
    const res = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`);
    const data = await res.json();

    return {
        movieInfo: data.results
    }
}

export default List;
