import Fetch from 'isomorphic-unfetch';
import Layout from "../components/Layout";
import { API_KEY } from '../API_KEY';

const List = props => {
    console.log(props)
    return (
        <Layout>
            <h1 className="text-center">Movie list!</h1>
            <div className="grid">
                <div className="card mb-3 text-center">
                    <h3 className="card-header">{props.movieInfo[0].title}</h3>
                    <div className="card-body">
                        <h6 className="card-subtitle text-muted">Support card subtitle</h6>
                    </div>
                    <img src="..." alt="Card image" />
                </div>
                <div className="card mb-3 text-center">
                    <h3 className="card-header">{props.movieInfo[1].title}</h3>
                    <div className="card-body">
                        <h6 className="card-subtitle text-muted">Support card subtitle</h6>
                    </div>
                    <img src="..." alt="Card image" />
                </div>
                <div className="card mb-3 text-center">
                    <h3 className="card-header">{props.movieInfo[2].title}</h3>
                    <div className="card-body">
                        <h6 className="card-subtitle text-muted">Support card subtitle</h6>
                    </div>
                    <img src="..." alt="Card image" />
                </div>
            </div>
            <style jsx>{`
                .grid {
                    display: grid;
                    grid-template-columns: 1fr 1fr 1fr;
                    grid-gap: 20px;
                }
                .card img {
                    height: 200px;
                    width: 100%;
                    display: block;
                }
            `}

            </style>
        </Layout>
    )
}

List.getInitialProps = async () => {
    const res = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`);
    const data = await res.json();

    return {
        movieInfo: data.results
    }
}

export default List;
