import Fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout';
import { API_KEY } from '../API_KEY';

const Shows = props => {
    return (
        <Layout>
            <h1 className="text-center">TV Shows List!</h1>
            <div className="grid">
                {props.showInfo.map(show => (
                    <div className="card mb-3 text-center">
                        <h3 className="card-header">{show.original_name.length > 18 ? show.original_name.substring(0, 18) + '...' : show.original_name}</h3>
                        <div className="card-body">
                            <h6 className="card-subtitle text-muted">{show.overview.length > 300 ? show.overview.substring(0, 300) + '...' : show.overview}</h6>
                        </div>
                        <img src={`https://image.tmdb.org/t/p/original/${show.backdrop_path}`} alt="Card image" />
                    </div>
                ))}
            </div>
            <style jsx>{`
                h1 {
                    margin: 20px auto;
                }
                .grid {
                    display: grid;
                    grid-template-columns: 1fr 1fr 1fr;
                    grid-gap: 15px;
                }
                .card img {
                    height: 200px;
                    width: 100%;
                    display: block;
                }
            `}

            </style>
        </Layout>
    )
}

Shows.getInitialProps = async () => {
    const res = await fetch(`https://api.themoviedb.org/3/tv/popular?api_key=${API_KEY}&language=en-US&page=1`);
    const data = await res.json();

    return {
        showInfo: data.results
    }
}

export default Shows;
